package MapLoader;

public class Mirror {
	
	private int x;
	private int y;
	private int height;
	private int width;
	

	public Mirror(int x, int y, int height, int width) {
		// TODO Auto-generated constructor stub
		this.x=x;
		this.y=y;
		this.height=height;
		this.width=width;
	}
	
	public int getX() {
		return x;
	}
	
	public int getY() {
		return y;
	}

}
